}

zx='<div id="zx" style="display:block">'+zxb+'</div>';
dx='<div id="dx" style="display:none">'+dxb+'</div>';


script='(function(){$(".button:first").addClass("upBtn");url=$(".button:first").val();video(url)})();\n$(".button").click(function(){id=$(this).attr("id");\nid="#"+id;\n$(".button").removeClass("upBtn");\n$(id).addClass("upBtn");\nurl=$(id).val();video(url)});\n$("li").click(function(){id=$(this).attr("id");\nid="#"+id;\n$("li").removeClass("active");\n$(id).addClass("active");\nif(id=="#b1"){$("#juji").show();$("#info").hide()}else{$("#info").show();$("#juji").hide()}});$("#dxb").click(function(){if($("#dxb").text()=="å€’åº"){$("#dxb").text("æ­£åº");$("#dx").show();$("#zx").hide();}else{$("#dxb").text("å€’åº");$("#dx").hide();$("#zx").show();}}) ';

